<style>
    .header-bottom.active .chime-bg{top: 78px} .garland{margin-top: 20px} div[class*="ball"] {display: block!important;}.chime-bg{position:absolute;width:230px;height:200px;border-top:1px solid rgba(128,128,128,.2);left:-45px;z-index:9}.chime-bg .chime{position:absolute;left:0;top:0;height:110px;width:24px;animation-name:rotate;animation-duration:2s;animation-delay:.7s;animation-iteration-count:infinite;animation-direction:linear;transform-origin:50% 0;animation-timing-function:linear}.chime-bg .chime:before{content:"";background-color:rgba(128,128,128,.2);position:absolute;height:100%;width:1px;left:calc(50% - 1px);top:0}.chime-bg .ball{width:24px;height:24px;background-color:#a12d1f;border-radius:50%;position:absolute;bottom:0;left:0}.chime-bg .ball:after{position:absolute;content:" ";width:85.9375%;height:85.9375%;background-color:#f34f4d;z-index:1;border-radius:50%;top:1px;left:2px}.chime-bg .small-ball{position:absolute;width:23.4375%;height:23.4375%;background:#fff;top:31.25%;left:23.4375%;z-index:2;border-radius:50%}.chime-bg .tiny{width:10.9375%;height:10.9375%;top:15.625%;left:46.875%}.chime-bg .red.red2{left:135px;height:150px;width:35px;animation-name:rotate5;animation-duration:2.5s;animation-delay:.3s;animation-iteration-count:infinite;animation-direction:linear;transform-origin:50% 0;animation-timing-function:linear}.chime-bg .red.red2 .ball{width:35px;height:35px}.chime-bg .orange{left:55px;height:140px;width:26px;animation-name:rotate5;animation-duration:3s;animation-delay:.1s;animation-iteration-count:infinite;animation-direction:linear;transform-origin:50% 0;animation-timing-function:linear}.chime-bg .orange .ball{width:25px;background-color:#e28124;height:25px}.chime-bg .orange .ball:after{background-color:#fdb868}.chime-bg .orange.orange2{left:195px;height:110px;width:25px;animation-name:rotate;animation-duration:1.5s;animation-delay:.7s;animation-iteration-count:infinite;animation-direction:linear;transform-origin:50% 0;animation-timing-function:linear}.chime-bg .orange.orange2 .ball{width:25px;height:25px}.chime-bg .blue{left:95px;height:115px;width:20px;animation-name:rotate10;animation-duration:1.5s;animation-delay:.5s;animation-iteration-count:infinite;animation-direction:linear;transform-origin:50% 0;animation-timing-function:linear}.chime-bg .blue .ball{width:20px;background-color:#2261a3;height:20px}.chime-bg .blue .ball:after{background-color:#3abbda}@keyframes rotate{0%{transform:rotate(-15deg);animation-timing-function:ease-in}25%{transform:rotate(0);animation-timing-function:ease-out}50%{transform:rotate(15deg);animation-timing-function:ease-in}75%{transform:rotate(0);animation-timing-function:ease-out}100%{transform:rotate(-15deg)}}@keyframes rotate10{0%{transform:rotate(-10deg);animation-timing-function:ease-in}25%{transform:rotate(0);animation-timing-function:ease-out}50%{transform:rotate(10deg);animation-timing-function:ease-in}75%{transform:rotate(0);animation-timing-function:ease-out}100%{transform:rotate(-10deg)}}@keyframes rotate5{0%{transform:rotate(-5deg);animation-timing-function:ease-in}25%{transform:rotate(0);animation-timing-function:ease-out}50%{transform:rotate(5deg);animation-timing-function:ease-in}75%{transform:rotate(0);animation-timing-function:ease-out}100%{transform:rotate(-5deg)}}@media (min-width: 768px){.header-bottom.active .chime-bg{top: 90px!important;}}@media (max-width: 767px) {.header-bottom.active .chime-bg{top: 90px}div.chime.red:first-child {display: none;}div.chime-bg {width: 170px;left: -8px;}.chime-bg .orange {left: 4px !important;} .chime-bg .blue {left: 50px !important;} .chime-bg .red.red2 {left: 85px !important;}  .chime-bg .orange.orange2 {left: 140px !important;}  }@media (min-width: 992px){.header-bottom.active .chime-bg{top: 78px!important;}}
</style>

